    // _({a:1,b:2,c:3}).pick(function(v,k){ return k=='a'||k=='b'}) => {a:1,c:3}
    pick : function(object, iterator) {
        return _(object).reduce(function(o, v, k){
            if (iterator(v,k))
                o[k] = v;
            return o;
        }, {});
    },
